mapper.select:
SELECT mid, mname, mpw, mnickNm, authority, enabled, mbirth, memail, mgender, mjoinDate, mphone, mno 
FROM Member 
WHERE mid = #{value}

mapper.update:
 
		Update member 
			set mname =#{mname} , mpw = #{mpw}, memail =#{memail},  
			 mphone = #{mphone} , mbirth = #{mbirth} , mgender = #{mgender} 
			where mid = #{mid}

 
		Update Member 
			set isWithdrawn = 1  
			where mid = #{mid}
