mapper.select:
SELECT mid, mname, mpw, mnickNm, authority, enabled, mbirth, memail, mgender, mjoinDate, mphone, mno 
FROM member 
WHERE mid = #{value}

mapper.update:
 
		Update member 
			set mname = #{mname} and mpw = #{mpw} and memail =#{memail} 
			and mphone = #{mphone} and mbirth = #{mbirth} and mgender = #{mgender} 
			where mid = #{mid}
